<?php
namespace Handyman\Core;

/**
 * Add theme support,
 * Register image sizes,
 * Theme's text domain
 * Logon page later,
 *
 * Class Theme_Init
 * @package Handyman\Core
 */
class Theme_Init
{

    public static $instance;


    public function __construct()
    {
        self::$instance =& $this;

        // Enable to load jQuery from the Google CDN. Dont move it from here!
        add_theme_support('jquery-cdn');

        /**
         * HTML5 markup for search forms
         */
        add_theme_support('html5', array('search-form'));


        /**
         * Theme setup
         */
        add_action('after_setup_theme', array($this, 'setup'), 20);


        /**
         * Add Page presets
         */
        add_filter('layers_preset_layouts', array($this, 'tlChildPresets'), 0);

        /**
         * Add additional classes to body tag
         */
        add_filter('body_class', array($this, 'setBodyClasses'));

        /**
         * Title filter
         */
        add_filter('wp_title', array($this, 'setTitle'), 10);

        /**
         * Customize HTML tag
         */
        add_filter('language_attributes', array($this, 'setHtmlClasses'));

        /**
         * Add Theme logo at login page
         */
        add_action('login_head', array($this, 'doLoginPage'));

        // Register optional & requirted plugins
        add_action('tgmpa_register', array($this, 'registerRequiredPlugins'));

        // Add Support for Contact Form7 plugin
        add_filter('wpcf7_form_class_attr', array($this, 'addCF7FormClass'));

        // Strip junk from Generated form
        add_filter('wpcf7_form_elements', array($this, 'stripBr'));

        add_filter('layers_body_class', array($this, 'layersBodyClass'));

        // Remove some templates for parent themes
        //add_filter('theme_page_templates', [$this, 'removePageTemplates']);

        // Pages requested with AJAX in popup
        add_action('template_include', array($this, 'ajaxTemplate'), 99);

        // Remove unsupported navigation locations defined in parent theme
         add_action('admin_init', array($this, 'unregisterUnsupportedMenuLocations'));
    }



    public function layersBodyClass($classes)
    {
        return $classes;
    }



    /**
     *  Remove unsupported navigation locations defined in parent theme
     */
    public function unregisterUnsupportedMenuLocations()
    {
        \Handyman\Extras\unregister_nav_menu('layers-footer');
    }




    /**
     * @param $title
     * @return mixed
     */
    public function setTitle($title)
    {
        return trim($title);
    }


    /**
     * @param $template
     * @return mixed
     */
    public function ajaxTemplate($template)
    {
        $popup = isset($_REQUEST['tl_popup']) && $_REQUEST['tl_popup'] == 1;

        if ($popup) {
            $template = 'single-ajax.php';
        }
        return $template;
    }


    /**
     * Main Theme setup
     */
    public function setup()
    {
        // Localize the theme
        load_child_theme_textdomain(TL_DOMAIN, TL_BASE_CHILD . '/languages');

        add_image_size('tl-square-small'    , 320, 320, true);
        add_image_size('tl-landscape-medium', 640, 400, true);
        add_image_size('tl-blog-landscape'  , 640, 280, true);

        register_nav_menus( array(
            LAYERS_THEME_SLUG . '-additional-primary' => __( 'Header Menu II' , 'layerswp' ),
        ) );
    }


    /**
     * Remove Custom Page templates from Parent theme
     *
     * @param $templates
     * @return mixed
     */
    public function removePageTemplates($templates)
    {
        return $templates;
    }


