     * @param $classes
     * @return array
     */
    public function setBodyClasses($classes)
    {
        global $tl_glob_mobile, $post;
        $classes = array_merge($classes, $tl_glob_mobile->detected());

        if (\Handyman\Extras\is_not_layers_page() || \Handyman\Extras\is_404_or_search()) {
            $classes[] = 'inner-page';
        }

        if(is_admin_bar_showing()){
            $classes[] = 'has-admin-bar';
        }
        return $classes;
    }


    /**
