    public function doLoginPage()
    {
        $css  = '';
        $logo = get_option( 'site_logo' );

        if($logo && isset($logo['url'])){
            $css = 'h1 a{ background-image: url(' . esc_url($logo['url']) . ') !important; }';
        }
        if($css){
            echo '<style type="text/css">' . $css . '</style>';
        }
    }


