    /**
     * Filter form generated by Form7 plugin
     *
     * @param $content
     * @return mixed
     */
    public function stripBr($content)
    {
        $content = str_replace(array('<br>', '<br/>', '<br />', '<label>', '</label>'), '', $content);
        $rand = rand(1, 1000);

        preg_match_all('/<input type="checkbox" [^>]+>/ui', $content, $matches);
        if ($matches) {
            if(isset($matches[0])){
                foreach($matches[0] as $k => $checkbox){
                    $checkbox_with_id = str_replace('<input type="checkbox"', '<input id="newsletter-subscription' . $rand . '" type="checkbox"', $checkbox);
                    $content          = str_replace($checkbox, $checkbox_with_id . '<label for="newsletter-subscription' . $rand . '"></label>' , $content);
                }
            }
        }
        return $content;
    }


